import React, { useState } from "react";
import {
  Box, Container, Grid, Typography, AppBar, Toolbar,
  IconButton, Button
} from "@mui/material";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import SaveIcon from "@mui/icons-material/Save";
import DownloadIcon from "@mui/icons-material/Download";
import FileUpload from "./components/FileUpload";
import PreviewPanel from "./components/PreviewPanel";
import ResultsPanel from "./components/ResultsPanel";
import SummaryPanel from "./components/SummaryPanel";
import axios from "axios";

export default function App() {
  const [uploading, setUploading] = useState(false);
  const [documentFile, setDocumentFile] = useState(null);
  const [results, setResults] = useState(null);

//   async function handleFile(file) {
//   setUploading(true);
//   setDocumentFile(file);
//   try {
//     const formData = new FormData();
//     formData.append("file", file);

//     // ðŸ‘‡ call your backend API
//     const res = await axios.post("http://localhost:5000/api/ocr", formData, {
//       headers: { "Content-Type": "multipart/form-data" },
//     });

//     // assume backend sends JSON: { fields: [...], summary: ... }
//     setResults(res.data);
//   } catch (err) {
//     console.error(err);
//     setResults({ error: "Validation failed" });
//   } finally {
//     setUploading(false);
//   }
// }

  function handleUpdateField(fieldName, newValue) {
    setResults((prev) => {
      if (!prev) return prev;
      return {
        ...prev,
        fields: prev.fields.map((f) =>
          f.field === fieldName ? { ...f, value: newValue, resolvedManually: true, status: f.status === 'error' ? 'warn' : f.status } : f
        )
      };
    });
  }

  function handleExport() {
    const blob = new Blob([JSON.stringify(results, null, 2)], {
      type: "application/json"
    });
    const href = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = href;
    a.download = "validated_output.json";
    a.click();
    URL.revokeObjectURL(href);
  }

  return (
    <Box sx={{ minHeight: "100vh", bgcolor: "background.default" }}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Health Insurance Document Validator
          </Typography>
          <IconButton color="inherit"><SaveIcon /></IconButton>
          <Button color="inherit" startIcon={<DownloadIcon />} onClick={handleExport}>
            Export
          </Button>
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ py: 3 }}>
        <Grid container spacing={2}>
          <Grid item xs={12} md={3}>
            <FileUpload onUpload={handleFile} uploading={uploading} />
            <Box mt={2}><PreviewPanel file={documentFile} /></Box>
            <Box mt={2}>
              <Typography variant="caption" color="text.secondary">
                Included: CMS-1500 template & validation scenarios in /assets
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} md={6}>
            <ResultsPanel results={results} onUpdateField={handleUpdateField} />
          </Grid>
          <Grid item xs={12} md={3}>
            <SummaryPanel results={results} />
            <Box mt={2}>
              <Button variant="contained" startIcon={<CloudUploadIcon />} fullWidth>
                Apply Suggestions
              </Button>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
}
